import { styled } from "@mui/material/styles";
import Typography from "@mui/material/Typography";
import { Box, Button } from "@mui/material";
import { Colors } from "../theme";
import banner from '../../images/banner.png';

export const BannerContainer = styled(Box)(({ theme }) => ({
  background: `url(${banner})`,
  width: "100%",
  height:"80vh",
//   marginTop:"50px",
  backgroundPosition: "center",
  backgroundRepeat: "no-repeat",
  backgroundSize: "cover",
  [theme.breakpoints.down("sm")]: {
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
  },
}));


export const BannerContent = styled(Typography)(({theme}) => ({
  lineHeight: 1.5,
  fontSize: "50px",
  textAlign:"center",
  fontWeight: "bold",
  display: "flex",
  flexDirection: "row",
  justifyContent: "center",
  alignItems: "center",
  wordBreak:"break-word",
  paddingX:"50px",
  whiteSpace:"normal",
  marginTop:"150px",
  [theme.breakpoints.down("sm")]: {
    marginTop:"0px"
  },

}));

export const BannerActionContainer = styled(Box)(() => ({
  // padding: "5px 30px 5px 30px",
  display:"flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
  

}));

export const BannerAction = styled(Button)(({ theme }) => ({
  color: Colors.primary,
  fontSize: "20px",
  [theme.breakpoints.down("sm")]: {
    width: "100%",
  },
}));

export const BannerImgContainer = styled(Box)(() => ({
  display: "flex",
  flexDirection: "row",
  justifyContent: "center",
  alignItems: "center",
  // padding: "30px 30px 5px 30px ",
  marginTop: "20px",
  width: "100%",
}));

export const HomeContent = styled(Box)(() => ({
  position: "absolute",
}));
