import React, { useContext } from "react";
import { styled, useTheme } from "@mui/material/styles";
import { useMediaQuery } from "@mui/material";
import {
  BannerAction,
  BannerActionContainer,
  BannerContainer,
  BannerContent,
  BannerImgContainer,
  BannerTitle,
  HomeContent,
} from "../styles/banner";
import { Box, Container, Grid, Typography } from "@mui/material";
import style from "../styles/css/style.css";
import Button from "@mui/material/Button";
import Link from "@mui/material/Link";
import { NavLink, useNavigate } from "react-router-dom";
import banner from "../images/banner.png";

function Banner() {
  const theme = useTheme();

  return (
    <>
      <BannerContainer sx={{ mt: 20 }}>
        <Container>
          <Grid container>
            <Grid item md={12} sm={6} xm={6} lg={12}>
            <BannerContent color="primary">
              Discover the best meats & spread in Everwhere with the loved ones
              ..... Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Totam officia eligendi corrupti velit repellat deleniti doloribus
              fugit vitae laborum modi placeat pariatur sit soluta alias et
              tenetur itaque, quam quidem.
            </BannerContent>
            </Grid>
            <Grid item md={12} sm={12} xm={12} lg={12}></Grid>
          </Grid>
        </Container>
      </BannerContainer>
    </>
  );
}

export default Banner;
